// src/Home.tsx
import React from 'react';
import { Link } from 'react-router-dom';

const Home: React.FC = () => {
    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>Welcome to the Book Store</h1>
            <p>Your one-stop destination for exploring and borrowing books!</p>

            <div style={{ marginTop: '20px' }}>
                <Link to="/login">
                    <button style={{ marginRight: '10px' }}>Login</button>
                </Link>
                <Link to="/signup">
                    <button>Sign Up</button>
                </Link>
            </div>
        </div>
    );
};

// Define styles with correct type
const styles: { [key: string]: React.CSSProperties } = {
    container: {
        textAlign: 'center',
        padding: '50px',
    },
    nav: {
        marginTop: '20px',
    },
    menu: {
        listStyleType: 'none',
        padding: 0,
    },
    menuItem: {
        margin: '10px 0',
    },
    link: {
        textDecoration: 'none',
        color: '#007BFF',
        fontSize: '20px',
    }
};

export default Home;
