import React, { useState } from 'react';

// Define the type for a Book
interface Book {
    id: number;
    title: string;
    author: string;
    genre: string;
    imageUrl: string;
}

// Define the props for the BookList component
interface BookListProps {
    books: Book[];
    handleEdit: (book: Book) => void;
    handleDelete: (id: number) => void;
}

const BookList: React.FC<BookListProps> = ({ books, handleEdit, handleDelete }) => {
    const [sortField, setSortField] = useState<keyof Book>('title'); // Default sort field
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc'); // Default sort order

    // Function to handle sorting
    const sortedBooks = [...books].sort((a, b) => {
        const aField = a[sortField].toString().toLowerCase();
        const bField = b[sortField].toString().toLowerCase();

        if (sortOrder === 'asc') {
            return aField < bField ? -1 : aField > bField ? 1 : 0;
        } else {
            return aField > bField ? -1 : aField < bField ? 1 : 0;
        }
    });

    // Function to toggle sorting order
    const toggleSort = (field: keyof Book) => {
        setSortField(field);
        setSortOrder(sortField === field && sortOrder === 'asc' ? 'desc' : 'asc');
    };

    return (
        <div>
            <h3>Book List</h3>
            <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                <thead>
                    <tr>
                        <th onClick={() => toggleSort('title')} style={{ cursor: 'pointer' }}>
                            Title {sortField === 'title' ? (sortOrder === 'asc' ? 'ðŸ”¼' : 'ðŸ”½') : ''}
                        </th>
                        <th onClick={() => toggleSort('author')} style={{ cursor: 'pointer' }}>
                            Author {sortField === 'author' ? (sortOrder === 'asc' ? 'ðŸ”¼' : 'ðŸ”½') : ''}
                        </th>
                        <th onClick={() => toggleSort('genre')} style={{ cursor: 'pointer' }}>
                            Genre {sortField === 'genre' ? (sortOrder === 'asc' ? 'ðŸ”¼' : 'ðŸ”½') : ''}
                        </th>
                        <th>Image</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {sortedBooks.map((b) => (
                        <tr key={b.id}>
                            <td>{b.title}</td>
                            <td>{b.author}</td>
                            <td>{b.genre}</td>
                            <td>
                                <img src={b.imageUrl} alt={b.title} style={{ width: '50px' }} />
                            </td>
                            <td>
                                <button onClick={() => handleEdit(b)}>Edit</button>
                                <button onClick={() => handleDelete(b.id)}>Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default BookList;
