// src/Home.tsx
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';

const Home: React.FC = () => {
    const navigate = useNavigate();
    const token = localStorage.getItem('token');
    const [userType, setUserType] = useState<string | null>(null);

    const isTokenValid = (token: string | null) => {
        if (!token) return false;

        try {
            const decoded: any = jwtDecode(token); // Decode the JWT
            const currentTime = Date.now() / 1000; // Current time in seconds TODO fix
            return decoded.exp > currentTime; // Check if the token is expired
        } catch (error) {
            console.error("Token validation error:", error);
            return false;
        }
    };

    const handleLogout = () => {
        // Clear any stored user data (e.g., token)
        localStorage.removeItem('token');
        // Redirect to login page or wherever you want
        navigate('/login');
    };

    const tokenIsValid = isTokenValid(token);
    // If token is not valid, redirect to login
    useEffect(() => {
        if (!tokenIsValid) {
            navigate('/login');
        } else {
            if(token){
                // If token is valid, extract user type
                const decoded: any = jwtDecode(token);
                setUserType(decoded.userType); // Assuming userType is in the token
            }
        }
    }, [token, navigate, tokenIsValid]);

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            {tokenIsValid && <LogoutBanner onLogout={handleLogout} />}

            <h1>Welcome to the Book Store</h1>
            <p>Your one-stop destination for exploring and borrowing books!</p>

            {/* Display different content based on user type */}
            {userType === 'Librarian' && (
                <div>
                    <h2>Hello Librarian!</h2>
                    <p>Manage your library and assist customers.</p>
                    <Link to="/manage-books">
                        <button style={{ margin: '10px' }}>Manage Books</button>
                    </Link>
                </div>
            )}
            {userType === 'Customer' && (
                <div>
                    <h2>Hello Customer!</h2>
                    <p>Browse and borrow your favorite books.</p>
                    <Link to="/browse-books">
                        <button style={{ margin: '10px' }}>Browse Books</button>
                    </Link>
                </div>
            )}
        </div>

    );
};

export default Home;
