// src/EditBook.tsx
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axiosConfig from '../utilities/axiosConfig';
import { Book, BookToSend } from '../types/Book';
import { getAuthHeader } from '../utilities/ApiUtils';

const EditBook: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();
    const [book, setBook] = useState<Book | null>(null);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const fetchBook = async () => {
            try {
                const response = await axiosConfig.get(`/api/books/${id}`);
                setBook(response.data);
            } catch (error) {
                console.error('Error fetching book:', error);
                setError('Failed to fetch book details.');
            }
        };

        fetchBook();
    }, [id]);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        if (book) {
            setBook({ ...book, [name]: value });
        }
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (book) {
            try {
                const config = getAuthHeader();
                // Prepare the book object for sending
                const bookToSend: BookToSend = {
                    title: book.title,
                    author: book.author,
                    coverImage: book.imageUrl,
                    description: book.description,
                    publisher: book.publisher,
                    publicationDate: book.publicationDate.toISOString(),
                    category: book.category,
                    isbn: book.isbn,
                    pageCount: Number(book.pageCount),
                    isAvailable: book.isAvailable,
                    checkedOutDate: book.checkedOutDate?.toISOString(),
                    returnDate: book.returnDate?.toISOString()
                };
                // Remove properties that are undefined
                (Object.keys(bookToSend) as (keyof BookToSend)[]).forEach((key) => {
                    if (bookToSend[key] === undefined) {
                        delete bookToSend[key];
                    }
                });
                await axiosConfig.put(`/api/books/${id}`, bookToSend, config);
                navigate('/manage-books'); // Navigate back to the book list after editing
            } catch (error) {
                console.error('Error updating book:', error);
                setError('Failed to update book.');
            }
        }
    };

    // Conditional rendering
    if (book === null) {
        return <div>Loading...</div>; // Show a loading message while fetching
    }

    return (
        <form onSubmit={handleSubmit}>
            <h2>Edit Book</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <div>
                <label>Title:</label>
                <input type="text" name="title" value={book.title} onChange={handleChange} required />
            </div>
            <div>
                <label>Author:</label>
                <input type="text" name="author" value={book.author} onChange={handleChange} required />
            </div>
            <div>
                <label>Category:</label>
                <input type="text" name="category" value={book.category} onChange={handleChange} required />
            </div>
            <div>
                <label>Cover Image:</label>
                <input type="text" name="coverImage" value={book.imageUrl} onChange={handleChange} />
            </div>
            <div>
                <label>Description:</label>
                <textarea name="description" value={book.description} onChange={handleChange} required />
            </div>
            <div>
                <label>ISBN:</label>
                <input type="text" name="isbn" value={book.isbn} onChange={handleChange} required />
            </div>
            <div>
                <label>Page Count:</label>
                <input type="number" name="pageCount" value={book.pageCount} onChange={handleChange} required />
            </div>
            <div>
                <label>Publisher:</label>
                <input type="text" name="publisher" value={book.publisher} onChange={handleChange} required />
            </div>
            <div>
                <label>Publication Date:</label>
                <input
                    type="date"
                    name="publicationDate"
                    value={book?.publicationDate.toISOString().split('T')[0] || ''} // Convert Date to string
                    onChange={handleChange}
                    required
                />
            </div>
            <button type="submit">Update Book</button>
        </form>
    );
};

export default EditBook;
