// src/ManageBooks.tsx
import React, { useEffect, useState } from 'react';
import axiosConfig from './axiosConfig';
import BookList from './BookList';
import { Book } from '../types/Book';
import { getAuthHeader } from '../utilities/ApiUtils';
//import { useNavigate } from 'react-router-dom';

const ManageBooks: React.FC = () => {
    const [books, setBooks] = useState<Book[]>([]);
    const [book, setBook] = useState<Book>({ id:-1, title: '', author: '', genre: '', imageUrl: '' });
    const [isEditing, setIsEditing] = useState(false);
    //const navigate = useNavigate();

    useEffect(() => {
        fetchBooks();
    }, []);

    const fetchBooks = async () => {
        try {
            const config = getAuthHeader(); 
            const response = await axiosConfig.get('/api/books/featured', config); // Fetch books from your API
            setBooks(response.data);
        } catch (error) {
            console.error('Error fetching books:', error);
            // Optionally set an error state here if needed
        }
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
        const { name, value } = e.target;
        setBook({ ...book, [name]: value });
    };

    const handleAddOrUpdateBook = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            if (isEditing) {
                await axiosConfig.put(`/api/books/${book.id}`, book); // Update existing book
            } else {
                await axiosConfig.post('/api/books', book); // Add new book
            }
            fetchBooks();
            resetForm();
        } catch (error) {
            console.error('Error saving book:', error);
        }
    };

    const handleEdit = (book: Book) => {
        setBook(book);
        setIsEditing(true);
    };

    const handleDelete = async (id: number) => {
        try {
            await axiosConfig.delete(`/api/books/${id}`); // Delete book by ID
            fetchBooks();
        } catch (error) {
            console.error('Error deleting book:', error);
        }
    };

    const resetForm = () => {
        setBook({ id: -1, title: '', author: '', genre: '', imageUrl: '' });
        setIsEditing(false);
    };

    return (
        <div>
            <h2>Manage Books</h2>
            <form onSubmit={handleAddOrUpdateBook}>
                <input
                    type="text"
                    name="title"
                    value={book.title}
                    onChange={handleChange}
                    placeholder="Title"
                    required
                />
                <input
                    type="text"
                    name="author"
                    value={book.author}
                    onChange={handleChange}
                    placeholder="Author"
                    required
                />
                <select name="genre" value={book.genre} onChange={handleChange} required>
                    <option value="">Select Genre</option>
                    <option value="Fiction">Fiction</option>
                    <option value="Non-Fiction">Non-Fiction</option>
                    <option value="Sci-Fi">Sci-Fi</option>
                    <option value="Biography">Biography</option>
                </select>
                <input
                    type="text"
                    name="imageUrl"
                    value={book.imageUrl}
                    onChange={handleChange}
                    placeholder="Image URL"
                    required
                />
                <button type="submit">{isEditing ? 'Update Book' : 'Add Book'}</button>
                <button type="button" onClick={resetForm}>Cancel</button>
            </form>

            <BookList books={books} handleEdit={handleEdit} handleDelete={handleDelete} />
        </div>
    );
};

export default ManageBooks;
