// src/ManageBooks.tsx
import React, { useEffect, useState } from 'react';
import axiosConfig from '../utilities/axiosConfig';
import BookList from './BookList';
import { Book } from '../types/Book';
import { getAuthHeader } from '../utilities/ApiUtils';
//import { useNavigate } from 'react-router-dom';

const ManageBooks: React.FC = () => {
    const [books, setBooks] = useState<Book[]>([]);
    const [isEditing, setIsEditing] = useState(false);
    //const navigate = useNavigate();

    useEffect(() => {
        fetchBooks();
    }, []);

    const fetchBooks = async () => {
        try {
            const config = getAuthHeader(); 
            const response = await axiosConfig.get('/api/books', config); // Fetch books from your API
            setBooks(response.data);
        } catch (error) {
            console.error('Error fetching books:', error);
            // Optionally set an error state here if needed
        }
    };

    // const handleEdit = (book: Book) => {
    //     setBook(book);
    //     setIsEditing(true);
    // };

    const handleDelete = async (id: number) => {
        try {
            const config = getAuthHeader(); 
            await axiosConfig.delete(`/api/books/${id}`, config); // Delete book by ID
            fetchBooks();
        } catch (error) {
            console.error('Error deleting book:', error);
        }
    };

    // Placeholder functions for editing and deleting books
    const handleEdit = (book: Book) => {
        // Implement edit functionality
        console.log('Edit book:', book);
    };

    return (
        <BookList books={books} handleEdit={handleEdit} handleDelete={handleDelete} />
    );
};

export default ManageBooks;
