import React from 'react';

const LogoutBanner: React.FC<{ onLogout: () => void }> = ({ onLogout }) => {
    return (
        <div style={{ backgroundColor: '#f8d7da', color: '#721c24', padding: '10px', textAlign: 'center' }}>
            <p>You are logged in. <button onClick={onLogout}>Logout</button></p>
        </div>
    );
};

export default LogoutBanner;
